<template>
  <div>
    <van-card
    :num="obj.num"
    :price="obj.price"
    :desc="obj.desc"
    :title="obj.title"
    >
    <template #num>
        <van-icon name="star-o" size="20" @click="shoucan" v-if="shows"/>
        <van-icon name="star" size="20" color="#1989fa" @click="notshou" v-else/>
    </template>
    <template #title>
       <b @click="info">{{ obj.title }}</b>
    </template>
    <template #thumb>
        <van-image
        width="90"
        height="90"
        lazy-load
        src="https://img01.yzcdn.cn/vant/ipad.jpeg"
        />
    </template>
    </van-card>
  </div>
</template>

<script>
import Vue from 'vue';
import { Lazyload } from 'vant';
import { mapActions } from 'vuex';

Vue.use(Lazyload);
export default {
    props:{
        obj:{
            type:Object,
            default(){
                return{}
            }
        }
    },
    data(){
        return{
            shows:true
        }
    },
    methods:{
        shoucan(){
            this.add_start_list(this.obj)
            this.shows=false
        },
        notshou(e){
            this.del_start_list(e)
            this.shows=true
        },
        info(){
            this.$emit('info', this.obj)
        },
        ...mapActions('starts',['add_start_list','del_start_list'])
    }
}
</script>

