import Vue from 'vue'
import VueRouter from 'vue-router'
import sy from '../views/sy.vue'

Vue.use(VueRouter)

const routes = [
  {
    path: '/',
    name: 'sy',
    component: sy,
  },
  {
    path: '/login',
    name: 'login',
    component: () => import( '../views/login.vue'),
  },
  {
    path: '/info/:id',
    name: 'info',
    component: () => import( '../views/info.vue'),
  }
]

const router = new VueRouter({
  routes
})


export default router
